  private int countKeys(Class<?> type) throws Exception {
    byte[] prefix = db.getTypeInfo(type).keyPrefix();
    int count = 0;

    try (DBIterator it = db.db().iterator()) {
      it.seek(prefix);

      while (it.hasNext()) {
        byte[] key = it.next().getKey();
        if (LevelDBIterator.startsWith(key, prefix)) {
          count++;
        }
      }
    }

    return count;
